import React, { useState } from 'react';
import axios from 'axios';

const API = process.env.REACT_APP_API || 'http://localhost:5000/api';

export default function App() {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('');
  const [theme, setTheme] = useState('light');
  // const [modalImage, setModalImage] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function submit(e) {
    e.preventDefault();
    setStatus('Sending...');
    try {
      await axios.post(`${API}/contact`, form);
      setStatus('Message sent ‚Äî thank you!');
      setForm({ name: '', email: '', message: '' });
    } catch (err) {
      setStatus('Failed to send. Try again later.');
    }
  }

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`page ${theme}`}>
      {/* Navbar */}
      <nav className="nav">
        <div className="logo">Nitesh Jindal</div>
        <ul className="nav-links">
          <li><a href="#about">About</a></li>
          <li><a href="#work">Work</a></li>
          <li><a href="#projects">Projects</a></li>
          <li><a href="#certifications">Certifications</a></li>
          <li><a href="#education">Education</a></li>
          <li><a href="#gallery">Gallery</a></li>
          <li><a href="#contact">Contact</a></li>
        </ul>
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
        </button>
      </nav>

      {/* Hero Section */}
      <header className="hero fade-in">
        <h1>Hi, I'm Nitesh Jindal</h1>
        <p>Web Developer ‚Ä¢ Software Developer ‚Ä¢ Problem solver</p>
        <div className="hero-cta">

          <a href="#contact" className="btn btn-outline"><i className="fas fa-envelope"></i> Contact Me</a>
          <a href="/resume.pdf" className="btn"><i className="fas fa-file-alt"></i> My Resume</a>
        </div>
      </header>

      {/* About */}
      <section id="about" className="about fade-in">
        <h2>About</h2>
        <p><strong>Languages & Frameworks:</strong> JavaScript, React.js, HTML, CSS, Python, C, Java</p>
        <p><strong>Core Concepts:</strong> Data Structures, Problem Solving</p>
        <p><strong>Databases:</strong> SQL, MongoDB</p>
      </section>

      {/* Work Experience */}
      <section id="work" className="workexperience fade-in">
        <h2>Work Experience</h2>
        <article className="Company glass">
          <h3>React Developer Intern - Celebal Technologies (June 2025 - August 2025)</h3>
          <p>Worked on front-end development using React.js for scalable web applications. Contributed to UI component design and optimized performance.</p>
          <a href="/Nitesh's_Certificate.pdf" target="_blank" rel="noreferrer">View</a>
        </article>
        <article className="Company glass">
          <h3>MERN Stack Intern - SRKWebApp Innovations LLP (May 2025 - July 2025)</h3>
          <p>Developed full-stack features using MongoDB, Express.js, React.js, and Node.js. Collaborated in a team environment using Git and Agile methodologies.</p>
          <a href="/Nitesh Jindal Certificate (1).pdf" target="_blank" rel="noreferrer">View</a>
        </article>
      </section>

      {/* Projects */}

<section id="projects" className="projects fade-in">
  <h2>Projects</h2>
  <div className="project-list">
    <article className="project glass">
      <h3>Online Blood Arranging System</h3>
      <p>Tech used: React, Node, MongoDB</p>
      <a href="#" className="view">
        <i className="fas fa-external-link-alt"></i> Live / Repo
      </a>
    </article>
    <article className="project glass">
      <h3>Portfolio Website</h3>
      <p>Tech used: React, CSS, Node, Mongo DB</p>
      <a href="#" className="view">
        <i className="fas fa-external-link-alt"></i> Live / Repo
      </a>
    </article>
  </div>
</section>

{/* Certifications */}
<section id="certifications" className="Certifications fade-in">
  <h2>Certifications</h2>
  <div className="Certificate-list grid">
    <article className="Certificate glass">
      <h3>Data Structures & Algorithms - ExcelR</h3>
      <a href="/Data structure.pdf" className="view" target="_blank" rel="noreferrer">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
    <article className="Certificate glass">
      <h3>Python Essential - LinkedIn Learning</h3>
      <a href="/python Certificate.pdf" className="view" target="_blank" rel="noreferrer">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
    <article className="Certificate glass">
      <h3>Cracking Coding Interviews: The Complete Guide - ExcelR </h3>
      <a href="/cracking coding.pdf" target="_blank" rel="noreferrer" className="view">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
    <article className="Certificate glass">
      <h3>DSA (Data Structures & Algorithms) - Linux World</h3>
      <a href="/linux world.pdf" target="_blank" rel="noreferrer" className="view">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
    <article className="Certificate glass">
      <h3>Project Excellence: Mastering Minor & Major Projects - ExcelR</h3>
      <a href="/project Excellence-Nitesh Jindal.pdf" target="_blank" rel="noreferrer" className="view">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
    <article className="Certificate glass">
      <h3>Foundations of Cloud, IoT, Edge & ML - NPTEL</h3>
      <a href="/Foundation of Cloud IoT Edge ML.pdf" target="_blank" rel="noreferrer" className="view">
        <i className="fas fa-eye"></i> View
      </a>
    </article>
  </div>
</section>


      {/* Education */}
      <section id="education" className="Education fade-in">
        <h2>Education</h2>
        <p>MCA - Poornima University, Jaipur (2024 ‚Äì 2026)</p>
        <p>B.Sc. in Mathematics - Maharishi Arvind University (2021 - 2024)</p>
      </section>

      {/* Gallery */}
      <section id="gallery" className="gallery fade-in">
        <h2>Photo Gallery</h2>
        <div className="gallery-grid">
          {['img1.jpg','img2.jpg','img3.jpg'].map((img, idx) => (
            <div key={idx} className="gallery-item" onClick={() => setModalImage(img)}>
              <img src={`/images/${img}`} alt={`Gallery ${idx}`} />
            </div>
          ))}
        </div>
      </section>

      {/* Modal */}
      {modalImage && (
        <div className="modal" onClick={() => setModalImage(null)}>
          <img src={`/images/${modalImage}`} alt="Enlarged" />
        </div>
      )}

{/* <section id="contact" className="contact fade-in">
  <h2>Contact</h2>
  <div className="contact-container">
    <div className="contact-details">
      <form onSubmit={submit} className="contact-form">
        <input
          name="name"
          placeholder="Your name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <textarea
          name="message"
          placeholder="Message"
          value={form.message}
          onChange={handleChange}
          required
